<body>
<h1 class="bt_title">Braintree Hosted Fields with Custom Stylesheet</h1>
<form id="bt_custom_form" name="bt_custom_form" method="post" action="">
  <div class="loader_container">
    <div class="loader_img loader"></div>
  </div>
  <div class="bt_form_wrap hide invisible">
    <fieldset>
      <label for="bt_card_number">Card Number</label>
      <div id="bt_card_number" class="inputFields"></div>
      <span class="payment-method-icon"></span>
    </fieldset>
    <div style="height: 84px;">
      <fieldset>
        <div style="float:left; width:49%">
          <label for="bt_exp_date">Expiration (MM/YY)</label>
          <div id="bt_exp_date" class="inputFields"></div>
        </div>
        <div style="float:right; width:49%">
          <label for="bt_cvv">CVV Number</label>
          <div id="bt_cvv" class="inputFields"></div>
          <span id="cvv-icon" class="payment-method-icon"></span> </div>
      </fieldset>
    </div>
    <div>
      <fieldset>
        <div style="float:left; width:49%; margin-top:9px">
          <input type="checkbox" value="1" name="braintree_subscribe" style="visibility:visible; position:relative;">
          <label for="braintree_subscribe">Set as default payment method</label>
        </div>
        <div style="float:right; width:49%" class="btn_container">
          <input type="submit" name="" class="button-me green-btn button-small pay-btn" value="Add Card" style="width:100%; margin-top:0px;" />
          <input type="hidden" name="amount" value="10"  />
          <span class="loader_img"></span>
        </div>
      </fieldset>
    </div>
  </div>
  <br />
  <br />
  <center>
    <div id="bt_paypal_container"></div>
  </center>
</form>
<script type="text/javascript" src="https://js.braintreegateway.com/js/beta/braintree-hosted-fields-beta.18.min.js"></script> 
<script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script> 
<!-- <script src="hosted_field_style.js"></script> -->
<script type="text/javascript">
  (function() {

  var BTFn = {};

  BTFn.sendJSON = function($obj) {

    $.ajax({
      dataType: "text",
      type: "POST",
      data: { action: "generateclienttoken"},
      url: "<?=base_url()?>welcome/braintree_hosted_fields_with_style_payment",
      success: function (req) {
        BTFn.initBT(req, $obj);
      },
      error: function() {
      }
    });
  };

  BTFn.initBT = function(req, $obj) {

    // we're on the custom fields tab
    braintree.setup(req, 'custom', {
      id: 'bt_custom_form',
      hostedFields: {
        number: {
          selector: '#bt_card_number'
        },
        cvv: {
          selector: '#bt_cvv'
        },
        expirationDate: {
          selector: '#bt_exp_date'
        },
        onFieldEvent: handleFieldEvent,
        styles: {
          '.invalid': {
          'color': '#D0041D'
          }
        }
      },
      onReady: function() {
        BTFn.showBtForm();
      },
      onError: function(error) {
        BTFn.showFormErrors(error);
        $obj.show().closest('.btn_container').find('.loader_img').hide();
      },
      paypal:{
        container:'bt_paypal_container',
      }
    });
  };

  BTFn.showBtForm = function() {

    var eAnimation = this.detectAnimationEvent();
    $('.loader_container').addClass('fadeOut');

    $('.loader_container').one(eAnimation, function(event) {
      $('.loader_container').remove();
      $('.bt_form_wrap').removeClass('hide');
      $('.bt_form_wrap').addClass('fadeIn').removeClass('invisible');
    });
  };

  BTFn.detectAnimationEvent = function() {

    var t, el = document.createElement("fakeelement");

    var animations = {
      "animation"      : "animationend",
      "OAnimation"     : "oAnimationEnd",
      "MozAnimation"   : "animationend",
      "WebkitAnimation": "webkitAnimationEnd"
    }

    for (t in animations){
      if (el.style[t] !== undefined){
        return animations[t];
      }
    }
  };

  BTFn.showFormErrors = function(error) {

    var inputMap = {
      'number' : '#bt_card_number',
      'cvv' : '#bt_cvv',
      'expirationDate' : '#bt_exp_date'
    };
    if(!error.details) {
      $('.inputFields').addClass('inputError');
    }else{
      var i, errorLength = error.details.invalidFieldKeys.length;
      $('.inputFields').removeClass('inputError');
      for(var i=0; i < errorLength; i++) {
        $(inputMap[error.details.invalidFieldKeys[i]]).addClass('inputError');
      }
    }
  };

  handleFieldEvent = function(event) {  
    //console.log((event.card));
    
    /* Other events
    if (event.type === "focus") {
      // Handle focus
    } else if (event.type === "blur") {
      // Handle blur
    } else if (event.type === "fieldStateChange") {
      // Handle a change in validation or card type
      console.log(event.isValid); // true|false
      if (event.card) {
      console.log(event.card.type);
      // visa|master-card|american-express|diners-club|discover|jcb|unionpay|maestro
      }
    }
    */
    
    if (event.type === "focus") {
      // Handle focus
      $('.inputFields').removeClass('inputError');
    }

    iconType = document.getElementsByClassName("payment-method-icon")[0];
    iconType.className = "payment-method-icon";
    if (event.card) iconType.className += " " + event.card.type;
  }

  $(document).ready(function() {

    var $pay_btn = $('.pay-btn');
    BTFn.sendJSON($pay_btn);
    $pay_btn.on('click', function() {
      $(this).hide().closest('.btn_container').find('.loader_img').css('display', 'inline-block');
    });
  });
})();
</script>
</body>
</html>